RSpec.describe InproceedingsController, type: :controller do

  # This should return the minimal set of attributes required to create a valid
  # Inproceeding. As you add validations to Inproceeding, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) {
    {reference:"IN10", author:"vilma vallaton", title:"laalalaa", booktitle:"aaab", year:2010}
  }

  let(:invalid_attributes) {
    {reference:"IN10", author:nil, title:"laalalaa", booktitle:"aaab", year:2010}
  }

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # InproceedingsController. Be sure to keep this updated too.
  let(:valid_session) { {} }

  describe "GET #index" do
    it "assigns all inproceedings as @inproceedings" do
      inproceeding = Inproceeding.create! valid_attributes
      get :index, {}, valid_session
      expect(assigns(:inproceedings)).to eq([inproceeding])
    end
  end

  describe "GET #show" do
    it "assigns the requested inproceeding as @inproceeding" do
      inproceeding = Inproceeding.create! valid_attributes
      get :show, {:id => inproceeding.to_param}, valid_session
      expect(assigns(:inproceeding)).to eq(inproceeding)
    end
  end

  describe "GET #new" do
    it "assigns a new inproceeding as @inproceeding" do
      get :new, {}, valid_session
      expect(assigns(:inproceeding)).to be_a_new(Inproceeding)
    end
  end

  describe "GET #edit" do
    it "assigns the requested inproceeding as @inproceeding" do
      inproceeding = Inproceeding.create! valid_attributes
      get :edit, {:id => inproceeding.to_param}, valid_session
      expect(assigns(:inproceeding)).to eq(inproceeding)
    end
  end

  describe "POST #create" do
    context "with valid params" do
      it "creates a new Inproceeding" do
        expect {
          post :create, {:inproceeding => valid_attributes}, valid_session
        }.to change(Inproceeding, :count).by(1)
      end

      it "assigns a newly created inproceeding as @inproceeding" do
        post :create, {:inproceeding => valid_attributes}, valid_session
        expect(assigns(:inproceeding)).to be_a(Inproceeding)
        expect(assigns(:inproceeding)).to be_persisted
      end

      it "redirects to the created inproceeding" do
        post :create, {:inproceeding => valid_attributes}, valid_session
        expect(response).to redirect_to(Inproceeding.last)
